import { AiOutlineClose, AiOutlineMenu } from "react-icons/ai";
import React, { useState } from "react";

const NavBar = () => {
  const [nav, setNav] = useState(false);
  const handleNav = () => {
    setNav(!nav);
  };

  return (
    <div className="flex justify-between items-center h-24 max-w-[1240px] mx-auto px-4 text-white">
      <h1 className="w-full text-3xl fond-bold text-[#00df9a]">
        Barbearia do Ratinho
      </h1>
      <ul className="hidden md:flex">
        <li className="p-4">Home</li>
        <li className="p-4">Sobre</li>
        <li className="p-4">Localização</li>
        <li className="p-4">Marcações</li>
        <li className="p-4">Contact</li>
      </ul>
      <dir onClick={handleNav} className="block md:hidden">
        {!nav ? <AiOutlineClose size={20} /> : <AiOutlineMenu size={20} />}
      </dir>
      <div
        className={
          !nav
            ? "fixed left-0 top-0 w-[60%] h-full border-r border-r-gray-900 bg-[#000300] ease-in-out duration-500"
            : "fixed left-[-100%]"
        }
      >
        <h1 className="w-full text-3xl fond-bold text-[#00df9a] m-4">
          Barbearia do Ratinho
        </h1>
        <ul className="uppercase p-4">
          <li className="p-4 border-b border-gray-600">Home</li>
          <li className="p-4 border-b border-gray-600">Sobre</li>
          <li className="p-4 border-b border-gray-600">Localização</li>
          <li className="p-4 border-b border-gray-600">Marcações</li>
          <li className="p-4">Contact</li>
        </ul>
      </div>
    </div>
  );
};

export default NavBar;
